{
  "title": "Guideline for the Structure of Gene and Protein Articles",
  "description": "This is a guideline for the structure of gene and protein articles on Wikipedia. It contains the articles naming conventions and the general recommended outline of an article, as well as useful information to bring an article to good article or featured article status.",
  "sections": {
    "General considerations": {
      "description": "The scope of a gene/protein article includes the human gene/protein (including all splice variants derived from that gene) and orthologs that exist in other species. Gene family articles should be created for paralogs.",
      "guidelines": [
        "Do not hype studies by listing names, credentials, or institutions of authors.",
        "Focus on what cited studies say about the structure, function, clinical significance, etc.",
        "Mention notable contributions in the discovery/history section."
      ]
    },
    "Article name": {
      "description": "Recommended naming conventions for articles.",
      "guidelines": [
        "Use the recommended UniProt protein name if relatively short.",
        "If the protein name is verbose, use a widely used protein acronym or the official HUGO gene symbol.",
        "Include the taxon in the title for viral proteins."
      ],
      "quote": {
        "text": "When naming proteins which can be grouped into a family based on homology or according to a notion of shared function, the different members should be enumerated with a dash followed by an Arabic number.",
        "source": {
          "url": "http://www.chem.qmul.ac.uk/iubmb/proteinName.html",
          "title": "Protein Naming Guidelines",
          "date": "",
          "work": "Recommendations on Biochemical & Organic Nomenclature, Symbols & Terminology etc.",
          "publisher": "International Union of Biochemistry and Molecular Biology"
        }
      }
    },
    "Gene nomenclature": {
      "description": "Abbreviations of genes are according to HUGO Gene Nomenclature Committee and written in italic font style.",
      "guidelines": [
        "Use abbreviations instead of full names.",
        "Write human gene names in capitals (e.g., ALDOA, INS).",
        "For orthologs, only the initial letter is capitalised."
      ],
      "usage_examples": [
        "the ALDOA gene is regulated...",
        "the rat gene for Aldoa is regulated...",
        "ALDOA is regulated..."
      ],
      "not_recommended": "the gene ALDOA is regulated."
    },
    "Images and diagrams": {
      "description": "Diagrams should keep to a standard format.",
      "guidelines": [
        "If the diagram guide does not provide sufficient guidance, suggest expansions to the standardized formatting."
      ]
    },
    "Infoboxes": {
      "description": "Infoboxes should be included at the top of each article.",
      "infoboxes": [
        {
          "template": "Infobox GNF protein",
          "description": "For genes/proteins for which an ortholog is present within the human genome.",
          "example_article": "Reelin",
          "template_filling_tool": {
            "url": "http://biogps.gnf.org/GeneWikiGenerator/#goto=welcome",
            "usage": "input: HUGO gene symbol"
          }
        },
        {
          "template": "Infobox protein",
          "description": "Smaller box appropriate for protein family articles.",
          "example_article": "Estrogen receptor",
          "template_filling_tool": {
            "url": "http://diberri.dyndns.org/cgi-bin/templatefiller/",
            "usage": "input: HGNC ID"
          }
        },
        {
          "template": "Infobox nonhuman protein",
          "description": "For proteins without a human ortholog.",
          "example_article": "Uterine serpin",
          "template_filling_tool": ""
        },
        {
          "template": "Infobox protein family",
          "description": "For protein families that are listed in Pfam.",
          "example_article": "T-box",
          "template_filling_tool": ""
        },
        {
          "template": "Infobox rfam",
          "description": "For RNA families that are listed in Rfam.",
          "example_article": "U1 spliceosomal RNA",
          "template_filling_tool": ""
        },
        {
          "template": "Infobox enzyme",
          "description": "For enzymes based on EC number.",
          "example_article": "Alcohol dehydrogenase",
          "template_filling_tool": ""
        }
      ],
      "guidance": [
        "If there is only one human paralog for a given EC number, add the corresponding enzyme infobox.",
        "If only one human paralog is assigned to a Pfam family, include a protein family infobox."
      ]
    },
    "Sections": {
      "description": "The recommended sections for the article.",
      "sections_list": {
        "Lead": "Defines the article's scope.",
        "Gene": "Specific information about the gene.",
        "Protein": "Specific information about the protein.",
        "Species, tissue, and subcellular distribution": "Optional section on species expression, tissue expression, and subcellular locations.",
        "Function": "Describes the function of the protein.",
        "Interactions": "Optional section for known protein interactions.",
        "Clinical significance": "Lists diseases related to mutations or protein deficiencies/excess.",
        "History/Discovery": "Lists names of individuals who made key discoveries."
      },
      "examples": [
        "Protein C",
        "Gonadotropin-releasing hormone",
        "Rubisco"
      ]
    },
    "Wikidata item": {
      "description": "Link to a Wikidata item of the entity mentioned in the lead section.",
      "guidance": "Link to the item of the protein rather than the gene."
    },
    "Citing sources": {
      "description": "Guidance on choosing and using reliable sources.",
      "guidelines": [
        "MCB articles should include inline citations.",
        "No substantial prose without specific citations.",
        "Format should be consistent within an article.",
        "Use citation templates for more streamlined citation management."
      ]
    },
    "Navigation box": {
      "description": "Articles about related proteins may be cross linked by including navigation boxes.",
      "examples": [
        "Cytoskeletal Proteins",
        "Ion channels",
        "Transcription factors",
        "Transmembrane receptors"
      ]
    },
    "Categories": {
      "description": "Every article should be added to at least one category.",
      "examples": [
        "Category:Proteins",
        "Category:Enzymes by function",
        "Category:Genes by human chromosome"
      ]
    },
    "Notes": "References list for notes and clarifications."
  },
  "categories": "WikiProject style advice",
  "subcategories": "Style guide"
}
